<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>		<title>Логин</title>		<style type="text/css">body {height:100%; margin:0; padding:0; text-align:center; background-color: #F5FCFF; align:center; line-height:0.95;}br {line-height:1px;}	div.logform {text-align:left; height:auto; width:700px; margin-left:auto; margin-right:auto; padding:150px 0px 100px 0;}	div.logform form div { font:20px arial; float:left; width:100px; text-align:right; padding:10px 15px 30px 0;}	div.logform form input {font:30px arial; margin-bottom:30px; margin-left:0; margin-right:0; width:500px;}	div.logform form input.auto {font:bold 21px arial; margin-top:10px; width:auto; float:left;}	div.logform span {font:bold 12px arial; color:#CC0000; padding:18px 0 0 20px; float:left;}div.bottlink {/*background:#ffddee;*/ padding:10px 0px 10px 0px; font:bold 14px arial; width:880px; text-align:right; margin-left:auto; margin-right:auto; }	div.bottlink a:link {color: #0000CC;}	div.bottlink a:visited {color: #0000CC;}	div.bottlink a:hover {color: #0097FF;}	</style>	<meta http-equiv="Content-Type" content="text/html; charset=windows-1251" /></head>	<body><!-- header --><!-- /header --><!-- main -->		<div class="logform">			<form			name="<?=$logformname ?>"			action="<?=$logformact ?>"			method="post"			onsubmit="return thrueForm();"			>				<div><?=$title_log ?></div>				<input type="text" name="<?=$logform_log ?>" value="<?=$logvalue_log ?>" onblur="thrueForm();" />				<br clear="all" />				<div><?=$title_pas ?></div>				<input type="password" name="<?=$logform_pas ?>" value="<?=$logvalue_pas ?>" />				<br clear="all" />				<div>&nbsp;</div>				<input class="auto" type="submit" name="<?=$logform_submit ?>" value="<?=$logvalue_submit ?>" />				<span class="authError"><?=$errtext ?></span>			</form>		</div><!-- /main --><!-- footer --><!-- /footer -->	</body></html><!-- Проверка формы --><script type="text/javascript">function thrueForm()	{		if((trueLog()) && (truePass()))		{//			document.<?=$logformname ?>.<?=$logform_submit ?>.disabled = false;			return true;		}		else		{//			document.<?=$logformname ?>.<?=$logform_submit ?>.disabled = true;			return false;		}	}function trueLog() //Проверка заполнения поля login	{var lenBodystr = document.<?=$logformname ?>.<?=$logform_log ?>.value.length; // Длина строки в полеvar trueSimbol = 0;var thesimbol;for(var thesimboln = 0; thesimboln < lenBodystr; ++thesimboln)		{	thesimbol = document.<?=$logformname ?>.<?=$logform_log ?>.value.charAt(thesimboln);	if((thesimbol != ";")&&(thesimbol != ":")&&(thesimbol != ",")&&(thesimbol != ".")&&(thesimbol != '\n')&&(thesimbol != " "))			{		++trueSimbol;			}		}if(trueSimbol < 1){return false;}// alert("Ошибка в этом поле"); else {return true;}	}function truePass() //Проверка заполнения поля password	{var lenBodystr = document.<?=$logformname ?>.<?=$logform_pas ?>.value.length; // Длина строки в полеvar trueSimbol = 0;var thesimbol;for(var thesimboln = 0; thesimboln < lenBodystr; ++thesimboln)		{	thesimbol = document.<?=$logformname ?>.<?=$logform_pas ?>.value.charAt(thesimboln);	if((thesimbol != ";")&&(thesimbol != ":")&&(thesimbol != ",")&&(thesimbol != ".")&&(thesimbol != '\n')&&(thesimbol != " "))			{		++trueSimbol;			}		}if(trueSimbol < 1){return false;}// alert("Ошибка в этом поле"); else {return true;}	}</script><!-- END: Проверка формы -->